package com.zed.base_demo.service.impl;import com.zed.base_demo.dao.UserMapper;import com.zed.base_demo.entity.User;import com.zed.base_demo.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;/** * @author:Zed * @date: 2019/5/2 */@Servicepublic class UserServiceImpl implements UserService {    @Autowired    private UserMapper userMapper;    @Override    public User findByUsername(String username) {        return userMapper.findByUsername(username);    }}