package com.zed.base_demo.shiro;import org.apache.shiro.authc.AuthenticationInfo;import org.apache.shiro.authc.AuthenticationToken;import org.apache.shiro.authc.UsernamePasswordToken;import org.apache.shiro.authc.credential.SimpleCredentialsMatcher;/** * @author:Zed * @date: 2019/5/2 */public class CredentialMatcher extends SimpleCredentialsMatcher {    /**     * 密码校验规则重写     */    @Override    public boolean doCredentialsMatch(AuthenticationToken token, AuthenticationInfo info) {        UsernamePasswordToken usernamePasswordToken = (UsernamePasswordToken) token;        String password=new String(usernamePasswordToken.getPassword());        String dbPassword=(String) info.getCredentials();        return this.equals(password,dbPassword);    }}