package com.zed.base_demo.utils;import org.springframework.beans.BeansException;import org.springframework.context.ApplicationContext;import org.springframework.context.ApplicationContextAware;import org.springframework.stereotype.Component;/** * 反射获取bean * @author:Zed * @date: 2019/5/9 */@Componentpublic class SpringBeanFactoryUtils implements ApplicationContextAware {    private static ApplicationContext context = null;    /**     * 从静态变量applicationContext中得到Bean, 自动转型为所赋值对象的类型.     */    public static <T> T getBean(Class<T> type) {        return context.getBean(type);    }    @Override    public void setApplicationContext(ApplicationContext applicationContext) throws BeansException {        if (SpringBeanFactoryUtils.context == null) {            SpringBeanFactoryUtils.context = applicationContext;        }    }}